import React from "react";
import { useSelector } from "react-redux";
import { selectArticles, filterArticles } from "../features/articles/articlesSlice";
import Search from "./Search";
import { Link } from 'react-router-dom';

export default function Articles () {
  const articles = useSelector(selectArticles);

  // Grab URLSearchParams object from useSearchParams hook
  // ... (code to get URLSearchParams and extract title)

  const title = ''; // Replace this with your logic to get the title from URLSearchParams

  const filteredArticles = title ? filterArticles(title, articles) : Object.values(articles)

  return (
    <main>
      <h1>Articles</h1>
      <ul>
        {filteredArticles.map(article => (
          <li key={article.slug}>
            {/* Use <Link> component to create links */}
            <Link to={`/${article.slug}`}>
              {article.title}
            </Link>
          </li>
        ))}
      </ul>
      <Search />
    </main>
  )
}
