import React, { useState } from "react";
import { signUp } from "../features/session/sessionSlice";
import { useDispatch } from "react-redux";
import { useHistory } from "react-router-dom"; // Import useHistory

export default function SignUp() {
  const [username, setUsername] = useState("");
  const dispatch = useDispatch();
  const history = useHistory(); // Get history object from useHistory

  const handleSubmit = e => {
    e.preventDefault();
    dispatch(signUp({ username: username })).then(() => {
      // Redirect to /profile after successful sign-up
      history.push("/profile");
    }).catch((error) => {
      // Handle error if sign-up fails
      console.error("Sign-up failed:", error);
      // Optionally, you can display an error message to the user
    });
  };

  return (
    <section>
      <h1>Sign Up</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Username
          <div>
            <input
              id="username"
              value={username}
              onChange={(e) => setUsername(e.currentTarget.value)}
            />
            <button type="submit" className="primary">
              Sign Up
            </button>
          </div>
        </label>
      </form>
    </section>
  );
}
